CREATE TABLE IF NOT EXISTS "ACCOUNT"(
    "AMOUNT_BALANCE" NUMERIC(12, 2) NOT NULL,
    "ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL,
    "NAME" CHARACTER VARYING(255) NOT NULL,
    "CURRENCY_CODE" ENUM('AUD', 'CNY', 'USD') NOT NULL,
    "CREATED_AT" TIMESTAMP(6) NOT NULL,
    "UPDATED_AT" TIMESTAMP(6)
);

CREATE TABLE IF NOT EXISTS "FUND_TRANSFER"(
    "AMOUNT" NUMERIC(7, 2) NOT NULL,
    "ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL,
    "SOURCE_ACCOUNT" BIGINT NOT NULL,
    "TARGET_ACCOUNT" BIGINT NOT NULL,
    "CURRENCY_CODE" ENUM('AUD', 'CNY', 'USD') NOT NULL,
    "CREATED_AT" TIMESTAMP(6) NOT NULL,
    "RESULT_CODE" CHARACTER VARYING(255) NOT NULL
);